<?php

namespace GAP\StockManagementBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * BesoinRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class BesoinRepository extends EntityRepository {

    public function getAll() {

        $qb = $this->createQueryBuilder('b')
            ->leftJoin('b.service', 's')
            ->leftJoin('b.molecule', 'm')
            ->addSelect('s')
            ->addSelect('m')
            ->orderBy('b.date', 'DESC');


        return $qb->getQuery()->getResult();
    }
    public function getAllByUser($idUsername) {

        $qb = $this->createQueryBuilder('b')
            ->leftJoin('b.service', 's')
            ->leftJoin('b.molecule','m')
            ->leftJoin('b.user', 'u')
            ->addSelect('s')
            ->addSelect('m')
            ->addSelect('u')
            ->where('u.id = ?1')
            ->setParameter(1, $idUsername);

        return $qb->getQuery()->getResult();
    }
    public function getAllByNoAffect() {
        $qb = $this->createQueryBuilder('b')
            ->leftJoin('b.service', 's')
            ->leftJoin('b.molecule', 'm')
            ->leftJoin('b.user', 'u')
            ->addSelect('s')
            ->addSelect('m')
            ->orderBy('b.date', 'DESC')
            ->where('b.user = NULL');
    }
    public function getAllWithMedic() {
        $qb = $this->createQueryBuilder('b')
                   ->leftJoin('b.service', 's')
                   ->leftJoin('b.molecule', 'm')
                   ->leftJoin('m.medicament','me')
                   ->addSelect('s')
                   ->addSelect('m')
                   ->addSelect('me')
                   ->orderBy('b.date', 'DESC');

        return $qb->getQuery()->getResult();
    }
}
